package myfirstjavaapp;

public class Breakstatment3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// use for loop
		for (int i = 4; i < 20; i++) {

			// use if statment
			if (i == 7) {
				break; // break loop
			}
			System.out.println(i);

		}
		System.out.println("Control will Come out from Loop ");

	}

}
